define('only-numbers',['app-modules'], function (modules) {

    return modules.$$directives.directive('onlyNumbers', [function () {

            return {
                require: '?ngModel',
                link: function (scope, element, attrs, ngModelCtrl) {
                    if (!ngModelCtrl) {
                        return;
                    }

                    ngModelCtrl.$parsers.push(function (val) {
                        var regexp = /[^0-9]+/g;
                        if (attrs.decimal !== undefined) {
                            regexp = /[^0-9\.]+/g;
                        }
                        var clean = val.replace(regexp, '');
                        if (val !== clean) {
                            ngModelCtrl.$setViewValue(clean);
                            ngModelCtrl.$render();
                        }
                        return clean;
                    });

                    var keyPress = function (event) {
                        if (event.keyCode === 32) {
                            event.preventDefault();
                        }
                    };

                    element.bind('keypress', keyPress);

                    var listenDestroy = scope.$on('$destroy', function () {
                        element.unbind('keypress', keyPress);
                        listenDestroy();
                    });
                }
            };


        }]);

});
